const items = $input.all();
const splitItems = $('Split Results1').all();
const results = [];
for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const splitItem = splitItems[i];
  const repo = splitItem?.json?.repository;
  if (!repo) continue;
  try {
    let txt = item.json.data || item.json;
    let wf = null;
    if (typeof txt === 'string') {
      try { wf = JSON.parse(txt); } catch { continue; }
    } else if (typeof txt === 'object' && txt.nodes) {
      wf = txt;
    } else { continue; }
    const nodes = wf?.nodes || [];
    if (!nodes.length) continue;
    const filePath = splitItem.json.path || '';
    const workflowName = wf.name || filePath.split('/').pop().replace(/\.json$/,'') || 'Untitled Workflow';
    const nodeTypes = [...new Set(nodes.map(n => n.type).filter(Boolean))];
    const nodeTypeNames = nodeTypes.map(t => String(t).toLowerCase());
    const hasAI = nodeTypeNames.some(t => t.includes('openai') || t.includes('langchain') || t.includes('agent') || t.includes('anthropic') || t.includes('gemini') || t.includes('groq') || t.includes('claude'));
    const keyNodes = nodes.slice(0,3).map(n => n.name).filter(Boolean).join(', ');
    results.push({ json: {
      workflow_name: workflowName,
      repository: repo.full_name || '',
      stars: repo.stargazers_count || 0,
      nodes: nodes.length,
      node_types: nodeTypes.length,
      has_ai: hasAI ? 'Yes' : 'No',
      key_nodes: keyNodes || 'N/A',
      description: (repo.description || '').substring(0,200),
      repo_url: repo.html_url || '',
      file_url: splitItem.json.html_url || '',
      _sort_stars: repo.stargazers_count || 0
    }});
  } catch {}
}
results.sort((a,b)=>b.json._sort_stars-a.json._sort_stars);
return results.length ? results : [{json:{error:'No workflows found',message:'Adjust the search query'}}];

let workflows = $json.data || [];
let topCount = ($nodes['Config'].data?.json?.ai_analysis_top) || 10;
const toS = v => (v==null?'':String(v));
const clean = (s,max=240)=>toS(s).replace(/[^\x00-\x7F]/g,'').replace(/\s+/g,' ').trim().slice(0,max);
const n = v => Number.isFinite(Number(v))?Number(v):0;
const top = workflows.slice(0, topCount);
const items = top.map((w,i)=>`${i+1}. ${clean(w.workflow_name,120)}
   Repository: ${clean(w.repository,120)}
   Nodes: ${n(w.nodes)}, Stars: ${n(w.stars)}
   Key Nodes: ${clean(w.key_nodes,160)}
   Description: ${clean(w.description,220)}`);
const prompt = `Analyze the following n8n workflows and provide brief insights.
Always respond in ENGLISH (en-US) only. If any input text is not English, translate it to concise English first.

${items.join('

')}

For each workflow, provide:
- description: one sentence (<= 25 words)
- use_case: one sentence (<= 20 words)
- difficulty: one of ["Beginner","Intermediate","Advanced"]

STRICT OUTPUT REQUIREMENTS:
- Return ONLY a valid JSON array (no markdown)
- ASCII characters only
- Schema example: [{"index":1,"description":"...","use_case":"...","difficulty":"Beginner"}]`;
return [{json:{prompt, all_workflows:workflows, analyzed_count: top.length, total_count: workflows.length}}];

const aiOutput = $input.first().json; const prep = $('Prepare AI Analysis1').first().json; const all = prep.all_workflows||[]; const topN = Number(prep.analyzed_count||0);
let arr=[]; try{const raw=aiOutput.output??aiOutput; arr=Array.isArray(raw)?raw:JSON.parse(raw);}catch{arr=[]}
const map=new Map(); for(const it of arr){ if(it&&Number.isFinite(Number(it.index))) map.set(Number(it.index)-1,it); }
const S=v=>v==null?'':String(v); const clean=s=>S(s).replace(/[^\x00-\x7F]/g,'').replace(/\s+/g,' ').trim();
return all.map((wf,i)=>{ const a=i<topN?map.get(i):undefined; const aiDesc=clean(a?.description||''); const finalDesc=clean(wf.description||'')||aiDesc; const use=i<topN?clean(a?.use_case||''):''; const diff=i<topN?clean(a?.difficulty||''):'';
  return {json:{workflow_name:wf.workflow_name,repository:wf.repository,stars:wf.stars,nodes:wf.nodes,node_types:wf.node_types,has_ai:wf.has_ai,key_nodes:wf.key_nodes,description:finalDesc,repo_url:wf.repo_url,file_url:wf.file_url,ai_description:aiDesc,ai_use_case:use,ai_difficulty:diff}}; });
